package control;

public class Ex03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Enter 1 number => print English text arcoding to number
		int number = 1;
		
		switch (number) {
		case 1:
			System.out.println("ONE");
			break;
		case 2:
			System.out.println("TWO");
			break;	
			
		default:
			System.out.println("Invalid number");
			break;
		}
	}

}